const regex = /([\S\s]){1,80}\b/g;

function StringDisplay(props) {
  const lines = props.length.match(regex);

  onDragOver = (ev) => {
    ev.preventDefault();
  };

  return (
    <div>
      {lines.map((line, i) => (
        <div key={i} draggable onDragOver={(e) => this.onDragOver(e)}>
          {line} {line.length} {i}
        </div>
      ))}
    </div>
  );
}

export default StringDisplay;
