import React, { Component } from "react";

const regex = /([\S\s]){1,80}\b/g;

class StringSplitter extends Component {
  constructor(props) {
    super(props);
    this.state = {
      lines: this.props.length.match(regex),
    };
  }

  onDragOver = (e) => {
    e.preventDefault();
  };

  onDragStart = (e, id) => {
    console.log("drag started for: " + id);
    e.dataTransfer.setData("id", id);
  };

  onDrop = (e, id) => {
    let oldId = e.dataTransfer.getData("id");
    console.log("dropped " + oldId + " on " + id);

    this.setState({
      lines: reorderLines(id, oldId),
    });
  };

  reorderLines = (id, oldId) => {
    var tempLines = this.state.lines;
    var a = tempLines[oldId];
    tempLines[oldId] = tempLines[id];
    tempLines[id] = a;

    console.log(tempLines);
    return tempLines;
  };

  render() {
    return (
      <div>
        {this.state.lines.map((line, i) => (
          <div
            key={line}
            draggable
            onDragOver={(e) => this.onDragOver(e)}
            onDragStart={(e) => this.onDragStart(e, i)}
            onDrop={(e) => this.onDrop(e, i)}
          >
            {line} {line.length} {i}
          </div>
        ))}
      </div>
    );
  }
}

export default StringSplitter;
