import React, { Component } from "react";

const regex = /([\S\s]){1,80}\b/g;

export default class StringSplitter extends Component {
  onDragOver = (ev) => {
    ev.preventDefault();
  };

  StringDisplay(props) {
    const lines = props.length.match(regex);

    return (
      <div>
        {lines.map((line, i) => (
          <div key={i} draggable onDragOver={(e) => this.onDragOver(e)}>
            {line} {line.length} {i}
          </div>
        ))}
      </div>
    );
  }

  render() {
    <StringDisplay
      length="Lorem ipsum dolor sit amet, consectetur adipiscing elit. Curabitur eleifend, 
  nunc in sodales fringilla, nulla orci fringilla purus, sit amet luctus lectus nunc a diam. 
  Ut orci metus, lacinia nec sodales nec, tempus vitae risus. Phasellus maximus fringilla suscipit. 
  Nulla lobortis arcu vitae arcu tempor, a auctor risus imperdiet. Vivamus id auctor orci. Praesent 
  nisl tortor, mollis id felis at, blandit pulvinar felis. In ultricies massa sit amet posuere finibus. 
  Nam egestas ut purus in finibus. Suspendisse quam tortor, imperdiet in gravida a, ultricies vitae est. 
  Curabitur bibendum sapien leo, eget viverra diam ultrices blandit. Nam porttitor quam vel nisi 
  condimentum iaculis. Proin sodales risus sit amet eros pretium fringilla. Vestibulum ante ipsum 
  primis in faucibus orci luctus et ultrices posuere cubilia curae; Donec tristique auctor est, nec 
  facilisis nisl ultricies quis."
    />;
    return null;
  }
}
