const regex = /([\S\s]){1,80}\b/g;

function StringDisplay(props) {
  const lines = props.string.match(regex);

  return (
    <div>
      {lines.map((line) => (
        <div>
          {line} {line.length}
        </div>
      ))}
    </div>
  );
}

export default StringDisplay;
