import React, { Component } from "react";

const regex = /([\S\s]){1,80}\b/g;

export default class StringSplitter extends Component {
  onDragOver = (ev) => {
    ev.preventDefault();
  };

  StringDisplay(props) {
    const lines = props.length.match(regex);

    return (
      <div>
        {lines.map((line, i) => (
          <div key={i} draggable onDragOver={(e) => this.onDragOver(e)}>
            {line} {line.length} {i}
          </div>
        ))}
      </div>
    );
  }

  render() {
    return null;
  }
}
