import React, { Component } from "react";
import debounce from "lodash.debounce";

const regex = /([\S\s]){1,80}\b/g;

class StringDisplay extends Component {
  constructor(props) {
    super(props);
    this.state = {
      lines: this.props.length.match(regex),
      saved: true,
    };
  }

  onDragOver = (e) => {
    e.preventDefault();
  };

  onDragStart = (e, index) => {
    e.dataTransfer.setData("draggedLineIndex", index);
  };

  onDrop = (e, index) => {
    var draggedLineIndex = e.dataTransfer.getData("draggedLineIndex");

    this.setState({
      lines: this.reorderLines(index, draggedLineIndex),
      saved: false,
    });

    this.updateDatabase();
  };

  updateDatabase = debounce(() => {
    fetch("https://jsonplaceholder.typicode.com/posts", {
      method: "POST",
      body: JSON.stringify({
        body: this.state.lines,
      }),
      headers: {
        "Content-type": "application/json; charset=UTF-8",
      },
    })
      .then((response) => response.json())
      .then((json) => console.log(json));

    this.setState({
      saved: true,
    });
  }, 2000);

  reorderLines(index, draggedLineIndex) {
    var tempLines = this.state.lines;
    var draggedLine = tempLines[draggedLineIndex];
    tempLines[draggedLineIndex] = tempLines[index];
    tempLines[index] = draggedLine;

    return tempLines;
  }

  render() {
    return (
      <div>
        <div className={this.state.saved ? "saved" : "saving"}>
          <p>{this.state.saved ? "Saved" : "Saving"}</p>
        </div>
        {this.state.lines.map((line, i) => (
          <div
            key={line}
            draggable
            onDragOver={(e) => this.onDragOver(e)}
            onDragStart={(e) => this.onDragStart(e, i)}
            onDrop={(e) => this.onDrop(e, i)}
          >
            {line}
          </div>
        ))}
      </div>
    );
  }
}

export default StringDisplay;
