import React, { Component } from "react";

const regex = /([\S\s]){1,80}\b/g;

let lines = {};

export default class StringSplitter extends Component {
  lines = this.props.length.match(regex);

  onDragOver = (ev) => {
    ev.preventDefault();
  };

  onDragStart = (ev, id) => {
    console.log("drag started for: " + id);
    ev.dataTransfer.setData("id", id);
  };

  onDrop = (ev, id) => {
    let oldId = ev.dataTransfer.getData("id");
    console.log("dropped " + oldId + " on " + id);
    var a = lines[oldId];
    lines[oldId] = lines[id];
    lines[id] = a;
    console.log(lines);
  };

  render() {
    return (
      <div>
        {lines.map((line, i) => (
          <div
            key={line}
            draggable
            onDragOver={(e) => this.onDragOver(e)}
            onDragStart={(e) => this.onDragStart(e, i)}
            onDrop={(e) => this.onDrop(e, i)}
          >
            {line} {line.length} {i}
          </div>
        ))}
      </div>
    );
  }
}
